/**
 * twoSumBruteForce: Finds two distinct numbers in an array that add up to a specific target value.
 *
 * This function uses a brute-force O(N^2) approach, checking every possible pair.
 *
 * @param {number[]} nums - The array of integers.
 * @param {number} target - The target sum.
 * @returns {number[] | null} An array containing the indices [index1, index2] of the two numbers,
 * or null if no such pair is found.
 */
function twoSumBruteForce(nums, target) {
  // Outer loop iterates through each element to be the first part of the pair.
  for (let i = 0; i < nums.length; i++) {
    // Inner loop iterates through the remaining elements to be the second part of the pair.
    // We start j at i + 1 to ensure we don't use the same element twice (i != j).
    for (let j = i + 1; j < nums.length; j++) {
      // Check if the current pair sums up to the target.
      if (nums[i] + nums[j] === target) {
        // If found, return the indices.
        return [i, j];
      }
    }
  }

  // If the loop completes without finding a solution, return null.
  return null;
}

// Example Usage
const numbers1 = [2, 7, 11, 15];
const target1 = 9;
// Expected result: [0, 1] because nums[0] + nums[1] = 2 + 7 = 9

const numbers2 = [3, 2, 4];
const target2 = 6;
// Expected result: [1, 2] because nums[1] + nums[2] = 2 + 4 = 6

const numbers3 = [1, 5, 9, 12];
const target3 = 20;
// Expected result: null (no pair sums to 20)

console.log(
  `Array: [${numbers1}], Target: ${target1} -> Indices: ${twoSumBruteForce(
    numbers1,
    target1
  )}`
);
console.log(
  `Array: [${numbers2}], Target: ${target2} -> Indices: ${twoSumBruteForce(
    numbers2,
    target2
  )}`
);
console.log(
  `Array: [${numbers3}], Target: ${target3} -> Indices: ${twoSumBruteForce(
    numbers3,
    target3
  )}`
);
